import { useEffect, useState } from 'react'
import {
  faAngular,
  faCss3,
  faGitAlt,
  faHtml5,
  faJsSquare,
  faReact,
} from '@fortawesome/free-brands-svg-icons'
import Loader from 'react-loaders'
import AnimatedLetters from '../AnimatedLetters'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import './index.scss'
import js from '../../assets/images/logo3.png'


const About = () => {
  const [letterClass, setLetterClass] = useState('text-animate')

  useEffect(() => {
     setTimeout(() => {
      setLetterClass('text-animate-hover')
    }, 3000)
  }, [])

  return (
    <>
      <div className="container about-page">
        <div className="text-zone">
          <h1>
            <AnimatedLetters
              letterClass={letterClass}
              strArray={['F', 'r', 'o', 'n', 't', 'e', 'n', 'd']}
              idx={15}
            />
            <span> &nbsp;</span>
             <AnimatedLetters
              letterClass={letterClass}
              strArray={['T', 'i', 'p', 's']}
              idx={15}
            />
          </h1>
          <p class="tips-logo-cont">
            <a rel="tips-logo" href="tips/javascript/javascript.html"><img rel="tips-logo" class="tips-tech-logo" src="../../images/tips/javascript.png" alt="javascript" /></a>
            <a rel="tips-logo" href="tips/typescript/typescript.html"><img rel="tips-logo" class="tips-tech-logo" src="assets/images/tips/typescript.svg" alt="typescript" /></a>
            <a rel="tips-logo" href="tips/angular/angular.html"><img rel="tips-logo" class="tips-tech-logo" src="assets/images/tips/angular.png" alt="angular" /></a>
            <a rel="tips-logo" href="tips/react/react.html"><img rel="tips-logo" class="tips-tech-logo" src="assets/images/tips/react.png" alt="react" /></a>
            <a rel="tips-logo" href="tips/css/css.html"><img rel="tips-logo" class="tips-tech-logo" src="assets/images/tips/css.png" alt="css" /></a>
            <a rel="tips-logo" href="tips/rxjs/rxjs.html"><img rel="tips-logo" class="tips-tech-logo" src="assets/images/tips/rxjs.png" alt="rxjs" /></a>
          </p>      
        </div>
      </div>
      <Loader type="pacman" />
    </>
  )
}

export default About
